import React, { useState } from 'react';

import Dropdown from '../drop-down/drop-down.component';

const NavigationMenu = () => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const handleDropdownClick = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const handleDropdownItemClick = (item) => {
    // Handle dropdown item click logic
    console.log('Dropdown item clicked:', item.label);
    setIsDropdownOpen(false);
  };

  const dropdownItems = [
    { label: 'DVDS', link: '/collections/buy-dvds' },
    { label: 'DVD Bundles', link: '/collections/dvd-bundles' },
    // Add more dropdown items as needed
  ];

  return (
    <ul
      id="HeaderMenu-MenuList-2"
      className="mo_list_menu header__submenu list-menu list-menu--disclosure gradient caption-large motion-reduce global-settings-popup"
      role="list"
      tabIndex="-1"
    >
      <li>
        <a href="/collections/all" className="header__menu-item list-menu__item link link--text focus-inset caption-large">
          All
        </a>
      </li>
      <li className="dropdown">
        <a
          href="/#"
          className="header__menu-item list-menu__item link link--text focus-inset caption-large"
          onClick={handleDropdownClick}
        >
          Products
        </a>
        {isDropdownOpen && <Dropdown items={dropdownItems} handleItemClick={handleDropdownItemClick} />}
      </li>
      {/* Add more list items as needed */}
    </ul>
  );
};

export default NavigationMenu;







