import React, { useState } from 'react';
import Dropdown from '../drop-down/drop-down.shop.component';

const Shop = () => {
 const options = [
    {   name:'Shop',
        link:'/',
    },
    {
        subCategory:[
        {   
            title:'All',
            path:'/collections/all',

        },
        {
            title:'DVDS',
            path:'/collections/buy-dvds',

        },
        {
            title:'DVD Bundels',
            path:'/collections/dvd-bundles',

        },
        {
            title:'Video On Demand',
            path:'/collections/buy-video-on-demand',

        },
        {
            title:'Video On Demand Bundles',
            path:'/collections/bundles',

        },
        {
            title:'Books',
            path:'/collections/books-1',

        },
        {
            title:'Posters',
            path:'/products/puppy-fitness-poster',

        },
         {
            title:'Gear',
            path:'/collections/buy-gear',

        },
        {
            title:' Gift Cards',
            path:'/products/gift-cards',

        },
        {
            title:'Courses',
            path:'/pages/madcap-university-courses',

        },


        ]
    }
 ];

 const [selectedItem, setSelectedItem] = useState(options[0]);

 const handleOptionChange = (value) => {
    const item = options.find((path) => option.path === path);
    setSelectedItem(item);
 };

 return (
    <div>
      <h1>Shop</h1>
      <Dropdown
        options={options}
        defaultOption={options[0].value}
        onOptionChange={handleOptionChange}
      />
      <h2>Selected item: {selectedItem.label}</h2>
      <h3>Price: {selectedItem.price}</h3>
    </div>
 );
};

export default Shop;


