import React, { useState } from 'react';
import UsefulInformation from './UsefulInformation'; // Assuming this is the file where your data is defined

const UsefulInformationDropdown = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleItemClick = (path) => {
    // Add your logic to handle item click
    console.log(`Navigating to: ${path}`);
    // You can use React Router or any other navigation logic here
    // For this example, let's log the path to the console
  };

  return (
    <div className="dropdown">
      <button onClick={toggleDropdown} className="dropdown-button">
        Useful Information
      </button>
      {isOpen && (
        <ul className="dropdown-list">
          {UsefulInformation.map((item) => (
            <li key={item.title} onClick={() => handleItemClick(item.path)} className={item.cName}>
              {item.title}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default UsefulInformationDropdown;
