import dropDownIcon from '../../assets/caret-down.svg';
import React, { useState } from 'react';

const Dropdown = ({ options }) => {
  const [selectedOption, setSelectedOption] = useState(null);
  const [isOpen, setIsOpen] = useState(false);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleOptionClick = (option) => {
    setSelectedOption(option);
    setIsOpen(false);
    // Add your custom logic here
    console.log(<img src={dropDownIcon}width={40} height={20.89}/>,option)
    // console.log('Selected option:', option);
  };

  return (
    <div className="dropdown">
      <button onClick={toggleDropdown} className="dropdown-button">
        {selectedOption ? selectedOption.title : 'Select a category'}
      </button>
      {isOpen && (
        <ul className="dropdown-list">
          {options.map((option) => (
            <li key={option.title} onClick={() => handleOptionClick(option)}>
              {option.title}
            </li>
          ))}
        </ul>
      )}


    </div>
  );
};

export default Dropdown;





