import dropDownIcon from '../../assets/caret-down.svg';
import React, {useState} from 'react';
import './drop-down.styles.scss';
import { Link } from 'react-router-dom';
import Shop from '../shop/shop.component.jsx';
import {shopProducts} from '../shop/shop.component.jsx';


const DropDown = () =>{

    const [click,onClick] =useState (false);
    const handelClick = () => setClick(!click);
    {shopProducts .map((item,indec))}
    return(
        <>
        <ul onClick={handelClick}
        className={click ? 'dropdown-clicked': 'dropdown'}>
            {shopProducts((item,index) =>{
                return(
                    <li key={index}>
                        <Link className={item.cName} to={item.path} onClick={()=>
                        setClick(false)}
                    >
                        {item.title}    
                    </Link>
                    </li>
                )
            })}
        </ul>
        </>
    )
} 

export default DropDown;
